      var str1 = dataValue('form.module_name')(state)
        .toString()
        .replace(/_/g, ' ');
      return str1;
    }),
    field('Group__c', dataValue('form.sfid')),
    field('Program__c', dataValue('form.program')),
    field('CommCare_ID__c', dataValue('metadata.instanceID'))
