fn(state => {
  if (state.payloads.length == 0) return state;

  const {
    counselMap,
    reasonMapping,
    milestoneTypeMap,
    milestoneMap,
    nutritionMap,
    pregDangerMap,
    fpMethodMap,
    cleanChoice,
    handleMultiSelect,
    fetchReference,
  } = state;

  const householdMapping = [
    ...new Map(
      state.payloads
       .filter(
          p =>
            p.indices.parent.case_id  !== undefined &&
            p.indices.parent.case_id  !== ''
        )
        .map(p => {
          return {
            CommCare_Code__c:
              p.indices.parent.case_id || p.properties.parent_id,
          };
        })
        .map(h => [h.CommCare_Code__c, h])
